import React, { useState } from 'react';

const ClozeForm = ({ addQuestion }) => {
  const [text, setText] = useState('');
  const [blanks, setBlanks] = useState([]);

  const handleUnderline = (word) => {
    setBlanks([...blanks, word]);
  };

  const handleSubmit = () => {
    addQuestion({
      type: 'cloze',
      data: { text, blanks },
    });
    setText('');
    setBlanks([]);
  };

  return (
    <div className="border p-4 rounded-md shadow-md mb-4 bg-white">
      <h3 className="text-lg font-bold mb-2">Cloze Question</h3>
      <textarea
        className="w-full border border-gray-300 rounded-md p-2 mb-4 focus:ring focus:ring-blue-200"
        placeholder="Enter your text here"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <button
        className="btn-primary"
        onClick={() => handleUnderline('Sample')}
      >
        Add Blank
      </button>
      <div className="mt-2">
        <h4 className="font-semibold">Blanks</h4>
        {blanks.map((blank, index) => (
          <span key={index} className="text-blue-600">
            {blank}
          </span>
        ))}
      </div>
      <button className="btn-primary mt-4" onClick={handleSubmit}>
        Add Question
      </button>
    </div>
  );
};

export default ClozeForm;
