import React, { useState } from 'react';
import { useDrag, useDrop } from 'react-dnd';

const CategorizedForm = ({ addQuestion }) => {
  const [categories, setCategories] = useState([]);
  const [options, setOptions] = useState([]);

  const handleAddCategory = (category) => {
    setCategories([...categories, category]);  // Update categories state
  };

  const handleAddOption = (option) => {
    setOptions([...options, option]);  // Update options state
  };

  const [, drag] = useDrag({
    type: 'category',
    item: { name: 'category' },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  const [, drop] = useDrop({
    accept: 'category',
    drop: (item) => handleAddCategory(item),
  });

  return (
    <div>
      <div ref={drop} style={{ border: '1px dashed gray', padding: '10px' }}>
        <h3>Drag and drop categories here</h3>
        {categories.map((category, index) => (
          <div key={index} ref={drag} style={{ opacity: 0.5 }}>
            {category.name}
          </div>
        ))}
      </div>

      <div>
        <button onClick={() => handleAddOption('New Option')}>Add Option</button>
        {options.map((option, index) => (
          <div key={index}>
            {option}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategorizedForm;
