import React, { useState } from 'react';
import { useDrag, useDrop } from 'react-dnd';

const CategorizedForm = ({ addQuestion }) => {
  const [categories, setCategories] = useState([
    { name: 'Fruits', items: ['Apple', 'Banana'] },
    { name: 'Vegetables', items: ['Carrot'] },
  ]);
  const [options, setOptions] = useState(['Apple', 'Banana', 'Carrot']);

  // Drag and Drop handlers
  const [{ isDragging }, drag] = useDrag(() => ({
    type: 'ITEM',
    item: { name: 'Apple' },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  const [, drop] = useDrop(() => ({
    accept: 'ITEM',
    drop: (item) => {
      console.log(`Item dropped: ${item.name}`);
      // Logic to categorize the item
    },
  }));

  return (
    <div>
      <h3>Categorized Form</h3>
      <div>
        <h4>Categories:</h4>
        {categories.map((category, index) => (
          <div key={index}>
            <h5>{category.name}</h5>
            <ul>
              {category.items.map((item, itemIndex) => (
                <li key={itemIndex}>{item}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>

      <div ref={drop} style={{ border: '1px solid black', padding: '10px' }}>
        <h4>Drag Items Here</h4>
        {options.map((option, index) => (
          <div key={index} ref={drag} style={{ padding: '5px', border: '1px solid gray' }}>
            {option}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategorizedForm;
