import React, { useState } from 'react';

const ComprehensionForm = ({ addQuestion }) => {
  const [questionText, setQuestionText] = useState('');
  const [options, setOptions] = useState([]);
  const [correctAnswer, setCorrectAnswer] = useState('');

  const handleOptionChange = (index, event) => {
    const newOptions = [...options];
    newOptions[index] = event.target.value;
    setOptions(newOptions);
  };

  const handleAddOption = () => {
    setOptions([...options, '']);
  };

  const handleSubmit = () => {
    const question = {
      type: 'comprehension',
      data: {
        questionText,
        options,
        correctAnswer,
      },
    };
    addQuestion(question);
  };

  return (
    <div className="border p-4 mb-4">
      <h2 className="text-xl font-semibold mb-4">Comprehension Question</h2>
      <textarea
        value={questionText}
        onChange={(e) => setQuestionText(e.target.value)}
        placeholder="Paragraph"
        className="border p-2 mb-4"
      />

      <div>
        {options.map((option, index) => (
          <div key={index}>
            <input
              type="text"
              value={option}
              onChange={(e) => handleOptionChange(index, e)}
              placeholder={`Option ${index + 1}`}
              className="border p-2 mb-2"
            />
          </div>
        ))}
        <button type="button" onClick={handleAddOption} className="bg-blue-500 text-white mb-4">Add Option</button>
      </div>

      <button onClick={handleSubmit} className="bg-yellow-500 text-white mt-4">Add Question</button>
    </div>
  );
};

export default ComprehensionForm;
