import React, { useState } from 'react';

const ClozeForm = ({ addQuestion, formData }) => {
  const [clozeText, setClozeText] = useState('');
  const [blanks, setBlanks] = useState(['']);

  const handleAddBlank = () => {
    setBlanks([...blanks, '']);
  };

  const handleRemoveBlank = (index) => {
    setBlanks(blanks.filter((_, i) => i !== index));
  };

  const handleSave = () => {
    const question = {
      type: 'cloze',
      data: {
        text: clozeText,
        blanks,
      },
    };
    addQuestion(question); // Add new cloze question to formData
  };

  return (
    <div>
      <h2>Cloze Question</h2>

      {/* Text Editor for Cloze Question */}
      <div>
        <input
          type="text"
          value={clozeText}
          onChange={(e) => setClozeText(e.target.value)}
          placeholder="Enter the text"
        />
      </div>

      {/* Blank placeholders */}
      <div>
        <h3>Blanks</h3>
        {blanks.map((blank, index) => (
          <div key={index} className="flex items-center gap-2 mb-2">
            <input
              type="text"
              value={blank}
              onChange={(e) => {
                const newBlanks = [...blanks];
                newBlanks[index] = e.target.value;
                setBlanks(newBlanks);
              }}
              placeholder={`Blank ${index + 1}`}
            />
            <button onClick={() => handleRemoveBlank(index)}>Remove</button>
          </div>
        ))}
        <button onClick={handleAddBlank}>Add Blank</button>
      </div>

      {/* Save Button */}
      <button onClick={handleSave}>Save Cloze Question</button>
    </div>
  );
};

export default ClozeForm;
