import React, { useState } from 'react';

const ComprehensionForm = ({ addQuestion }) => {
  const [text, setText] = useState('');
  const [mcqs, setMcqs] = useState([{ question: '', options: [] }]);

  const handleAddMCQ = () => {
    setMcqs([...mcqs, { question: '', options: [] }]);
  };

  const handleSubmit = () => {
    addQuestion({
      type: 'comprehension',
      data: { text, mcqs },
    });
    setText('');
    setMcqs([{ question: '', options: [] }]);
  };

  return (
    <div className="border p-4 rounded-md shadow-md mb-4 bg-white">
      <h3 className="text-lg font-bold mb-2">Comprehension Question</h3>
      <textarea
        className="w-full border border-gray-300 rounded-md p-2 mb-4 focus:ring focus:ring-blue-200"
        placeholder="Enter the paragraph"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <button className="btn-primary" onClick={handleAddMCQ}>
        Add MCQ
      </button>
      <div className="mt-2">
        {mcqs.map((mcq, index) => (
          <div key={index} className="mb-4">
            <input
              type="text"
              className="w-full border border-gray-300 rounded-md p-2 mb-2"
              placeholder="Enter MCQ question"
              value={mcq.question}
              onChange={(e) => {
                const newMcqs = [...mcqs];
                newMcqs[index].question = e.target.value;
                setMcqs(newMcqs);
              }}
            />
            <textarea
              className="w-full border border-gray-300 rounded-md p-2"
              placeholder="Enter options separated by commas"
              onChange={(e) => {
                const newMcqs = [...mcqs];
                newMcqs[index].options = e.target.value.split(',');
                setMcqs(newMcqs);
              }}
            />
          </div>
        ))}
      </div>
      <button className="btn-primary mt-4" onClick={handleSubmit}>
        Add Question
      </button>
    </div>
  );
};

export default ComprehensionForm;
