import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api'; // Update to your API URL

export const saveFormData = async (endpoint, data) => {
  const response = await axios.post(`${API_BASE_URL}${endpoint}`, data);
  return response;
};
