import React, { useState } from 'react';

const ClozeForm = ({ addQuestion }) => {
  const [questionText, setQuestionText] = useState('');
  const [blanks, setBlanks] = useState([]);

  const handleUnderline = (start, end) => {
    const newBlank = { start, end };
    setBlanks([...blanks, newBlank]);
  };

  const handleSubmit = () => {
    const question = {
      type: 'cloze',
      data: {
        questionText,
        blanks,
      },
    };
    addQuestion(question);
  };

  return (
    <div className="border p-4 mb-4">
      <h2 className="text-xl font-semibold mb-4">Fill-in-the-Blank Question</h2>
      <textarea
        value={questionText}
        onChange={(e) => setQuestionText(e.target.value)}
        placeholder="Type your question here..."
        className="border p-2 mb-4"
      />

      <button onClick={handleSubmit} className="bg-yellow-500 text-white mt-4">Add Question</button>
    </div>
  );
};

export default ClozeForm;
