import React, { useState, useEffect } from "react";
import { saveFormData } from "../services/api";

const CategorizedForm = ({ addQuestion, initialQuestion }) => {
  const [questionText, setQuestionText] = useState("");
  const [categories, setCategories] = useState([]);
  const [options, setOptions] = useState([]);
  const [newOption, setNewOption] = useState("");
  const [newCategory, setNewCategory] = useState("");
  const [imageUrl, setImageUrl] = useState("");

  // Populate form with initial question data
  useEffect(() => {
    if (initialQuestion) {
      setQuestionText(initialQuestion.data.questionText || "");
      setCategories(
        initialQuestion.data.categories.map((cat) => ({
          name: cat,
          items: [],
        }))
      );
      setOptions(initialQuestion.data.options || []);
    }
  }, [initialQuestion]);

  // Add new category
  const addCategory = () => {
    if (newCategory.trim()) {
      setCategories([...categories, { name: newCategory, items: [] }]);
      setNewCategory("");
    }
  };

  // Remove category
  const removeCategory = (index) => {
    setCategories(categories.filter((_, i) => i !== index));
  };

  // Add new option
  const addOption = () => {
    if (newOption.trim()) {
      setOptions([...options, newOption]);
      setNewOption("");
    }
  };

  // Remove option
  const removeOption = (index) => {
    setOptions(options.filter((_, i) => i !== index));
  };

  // Assign option to category
  const assignOptionToCategory = (option, categoryIndex) => {
    const updatedCategories = [...categories];
    updatedCategories[categoryIndex].items.push(option);
    setCategories(updatedCategories);

    // Remove the option from the unassigned list
    setOptions(options.filter((opt) => opt !== option));
  };

  // Save the question
  const handleSave = async () => {
    const question = {
      type: "categorized",
      data: {
        questionText,
        categories,
        options,
        imageUrl, // Include the image URL
      },
    };

    try {
      const response = await saveFormData("/questions", question);
      console.log("Question saved:", response.data);

      // Add question to the form builder
      addQuestion(question);
    } catch (error) {
      console.error("Error saving question:", error);
    }
  };

  return (
    <div className="p-4 border rounded shadow-md mb-6">
      <h2 className="text-xl font-bold mb-4">Categorized Question</h2>

      {/* Categories */}
      <div className="mt-4">
        <h3 className="font-semibold mb-2">Categories</h3>
        {categories.map((category, index) => (
          <div key={index} className="border p-2 rounded mb-2">
            <div className="flex justify-between items-center mb-2">
              <input
                type="text"
                value={category.name}
                onChange={(e) => {
                  const updatedCategories = [...categories];
                  updatedCategories[index].name = e.target.value;
                  setCategories(updatedCategories);
                }}
                className="border rounded p-2 flex-grow"
                placeholder="Category Name"
              />
              <button
                onClick={() => removeCategory(index)}
                className="px-3 py-2 bg-red-500 text-white rounded"
              >
                Delete
              </button>
            </div>
            <ul className="list-disc ml-4">
              {category.items.map((item, i) => (
                <li key={i}>{item}</li>
              ))}
            </ul>
          </div>
        ))}
        <div className="flex gap-2">
          <input
            type="text"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
            placeholder="New Category"
            className="border rounded p-2 flex-grow"
          />
          <button
            onClick={addCategory}
            className="px-4 py-2 bg-blue-500 text-white rounded"
          >
            Add
          </button>
        </div>
      </div>

      {/* Options */}
      <div className="mt-4">
        <h3 className="font-semibold mb-2">Options</h3>
        {options.map((option, index) => (
          <div key={index} className="flex items-center gap-2 mb-2">
            <span className="border rounded p-2 flex-grow">{option}</span>
            {categories.length > 0 && (
              <select
                onChange={(e) =>
                  assignOptionToCategory(option, parseInt(e.target.value, 10))
                }
                defaultValue=""
                className="border p-2 rounded"
              >
                <option value="" disabled>
                  Assign to Category
                </option>
                {categories.map((cat, i) => (
                  <option key={i} value={i}>
                    {cat.name}
                  </option>
                ))}
              </select>
            )}
            <button
              onClick={() => removeOption(index)}
              className="px-3 py-2 bg-red-500 text-white rounded"
            >
              Delete
            </button>
          </div>
        ))}
        <div className="flex gap-2">
          <input
            type="text"
            value={newOption}
            onChange={(e) => setNewOption(e.target.value)}
            placeholder="New Option"
            className="border rounded p-2 flex-grow"
          />
          <button
            onClick={addOption}
            className="px-4 py-2 bg-blue-500 text-white rounded"
          >
            Add
          </button>
        </div>
      </div>

      {/* Save Button */}
      <button
        onClick={handleSave}
        className="mt-4 px-6 py-2 bg-green-500 text-white rounded"
      >
        Save Question
      </button>
    </div>
  );
};

export default CategorizedForm;
