import React, { useState } from 'react';

const CategorizedForm = ({ addQuestion }) => {
  const [categories, setCategories] = useState(['Category 1']);
  const [options, setOptions] = useState(['Option 1']);

  const handleAddCategory = () => setCategories([...categories, `Category ${categories.length + 1}`]);
  const handleAddOption = () => setOptions([...options, `Option ${options.length + 1}`]);

  const handleSubmit = () => {
    addQuestion({
      type: 'categorized',
      data: { categories, options },
    });
  };

  return (
    <div className="border p-4 rounded-md shadow-md mb-4 bg-white">
      <h3 className="text-lg font-bold mb-2">Categorized Question</h3>
      <div className="mb-2">
        <h4 className="font-semibold">Categories</h4>
        {categories.map((category, index) => (
          <div key={index} className="text-gray-700">{category}</div>
        ))}
        <button className="btn mt-2" onClick={handleAddCategory}>
          Add Category
        </button>
      </div>
      <div className="mb-2">
        <h4 className="font-semibold">Options</h4>
        {options.map((option, index) => (
          <div key={index} className="text-gray-700">{option}</div>
        ))}
        <button className="btn mt-2" onClick={handleAddOption}>
          Add Option
        </button>
      </div>
      <button className="btn-primary mt-4" onClick={handleSubmit}>
        Add Question
      </button>
    </div>
  );
};

export default CategorizedForm;
