import React, { useState } from 'react';
import CategorizedForm from '../components/CategorizedForm';
import ClozeForm from '../components/ClozeForm';
import ComprehensionForm from '../components/ComprehensionForm';
import FormRenderer from '../components/FormRenderer';

const FormBuilder = () => {
  const [questions, setQuestions] = useState([]);

  // Function to add a question to the form
  const addQuestion = (question) => {
    setQuestions([...questions, question]);
  };

  // Function to handle form submission, save the form data
  const handleSaveForm = () => {
    // You can use the `saveFormData` API here to send the form data to the backend
    console.log('Form Data:', questions);
  };

  return (
    <div className="container mx-auto p-6">
      <div>
        <h1 className="text-2xl font-bold mb-6">Form Builder</h1>
      </div>

      {/* Form Components to Add Questions */}
      <CategorizedForm addQuestion={addQuestion} />
      <ClozeForm addQuestion={addQuestion} />
      <ComprehensionForm addQuestion={addQuestion} />

      {/* Button to Save the Form */}
      <button 
        onClick={handleSaveForm} 
        className="bg-blue-500 text-white px-4 py-2 mt-4"
      >
        Save Form
      </button>

      {/* Render the Questions Added */}
      <div className="mt-6">
        <h2 className="text-xl font-semibold">Form Preview</h2>
        <FormRenderer questions={questions} />
      </div>
    </div>
  );
};

export default FormBuilder;
