import React, { useState } from 'react';

const CategorizedForm = ({ addQuestion }) => {
  const [questionText, setQuestionText] = useState('');
  const [categories, setCategories] = useState([{ name: 'Category 1', items: [] }]);
  const [options, setOptions] = useState([]);

  const handleCategoryChange = (index, event) => {
    const newCategories = [...categories];
    newCategories[index].name = event.target.value;
    setCategories(newCategories);
  };

  const handleItemChange = (categoryIndex, itemIndex, event) => {
    const newCategories = [...categories];
    newCategories[categoryIndex].items[itemIndex] = event.target.value;
    setCategories(newCategories);
  };

  const handleAddCategory = () => {
    setCategories([...categories, { name: '', items: [] }]);
  };

  const handleAddItem = (categoryIndex) => {
    const newCategories = [...categories];
    newCategories[categoryIndex].items.push('');
    setCategories(newCategories);
  };

  const handleAddOption = (event) => {
    setOptions([...options, event.target.value]);
  };

  const handleSubmit = () => {
    const question = {
      type: 'categorized',
      data: {
        questionText,
        categories,
        options,
      },
    };
    addQuestion(question);
  };

  return (
    <div className="border p-4 mb-4">
      <h2 className="text-xl font-semibold mb-4">Categorized Question</h2>
      <div>
        <label className="block mb-2">Question Text:</label>
        <input
          type="text"
          value={questionText}
          onChange={(e) => setQuestionText(e.target.value)}
          className="border p-2 w-full mb-4"
        />
      </div>
      
      <div>
        <label className="block mb-2">Categories:</label>
        {categories.map((category, index) => (
          <div key={index} className="mb-2">
            <input
              type="text"
              placeholder={`Category ${index + 1}`}
              value={category.name}
              onChange={(e) => handleCategoryChange(index, e)}
              className="border p-2 mb-2"
            />
            {category.items.map((item, itemIndex) => (
              <div key={itemIndex} className="flex items-center mb-2">
                <input
                  type="text"
                  value={item}
                  onChange={(e) => handleItemChange(index, itemIndex, e)}
                  className="border p-2 mb-2"
                />
              </div>
            ))}
            <button
              type="button"
              onClick={() => handleAddItem(index)}
              className="bg-blue-500 text-white px-4 py-2 mb-4"
            >
              Add Item
            </button>
          </div>
        ))}
        <button
          type="button"
          onClick={handleAddCategory}
          className="bg-green-500 text-white px-4 py-2"
        >
          Add Category
        </button>
      </div>
      
      <div>
        <label className="block mb-2">Options:</label>
        {options.map((option, index) => (
          <div key={index} className="flex items-center mb-2">
            <input
              type="text"
              value={option}
              onChange={(e) => setOptions(options.map((opt, idx) => (idx === index ? e.target.value : opt)))}
              className="border p-2 mb-2"
            />
          </div>
        ))}
        <button
          type="button"
          onClick={handleAddOption}
          className="bg-blue-500 text-white px-4 py-2 mb-4"
        >
          Add Option
        </button>
      </div>

      <button onClick={handleSubmit} className="bg-yellow-500 text-white px-4 py-2">
        Add Question
      </button>
    </div>
  );
};

export default CategorizedForm;
