import React, { useState } from 'react';

const ComprehensionForm = ({ addQuestion, formData }) => {
  const [comprehensionText, setComprehensionText] = useState('');
  const [mcqs, setMcqs] = useState([{ question: '', options: [''] }]);

  const handleAddMCQ = () => {
    setMcqs([...mcqs, { question: '', options: [''] }]);
  };

  const handleRemoveMCQ = (index) => {
    setMcqs(mcqs.filter((_, i) => i !== index));
  };

  const handleSave = () => {
    const question = {
      type: 'comprehension',
      data: {
        text: comprehensionText,
        mcqs,
      },
    };
    addQuestion(question); // Add new comprehension question to formData
  };

  return (
    <div>
      <h2>Comprehension Question</h2>

      {/* Text Editor for Comprehension Question */}
      <div>
        <input
          type="text"
          value={comprehensionText}
          onChange={(e) => setComprehensionText(e.target.value)}
          placeholder="Enter the passage text"
        />
      </div>

      {/* MCQ Questions */}
      <div>
        <h3>MCQ Questions</h3>
        {mcqs.map((mcq, index) => (
          <div key={index}>
            <input
              type="text"
              value={mcq.question}
              onChange={(e) => {
                const newMcqs = [...mcqs];
                newMcqs[index].question = e.target.value;
                setMcqs(newMcqs);
              }}
              placeholder="Enter question"
            />
            {mcq.options.map((option, i) => (
              <div key={i}>
                <input
                  type="text"
                  value={option}
                  onChange={(e) => {
                    const newMcqs = [...mcqs];
                    newMcqs[index].options[i] = e.target.value;
                    setMcqs(newMcqs);
                  }}
                  placeholder={`Option ${i + 1}`}
                />
              </div>
            ))}
            <button onClick={() => handleRemoveMCQ(index)}>Remove MCQ</button>
          </div>
        ))}
        <button onClick={handleAddMCQ}>Add MCQ</button>
      </div>

      {/* Save Button */}
      <button onClick={handleSave}>Save Comprehension Question</button>
    </div>
  );
};

export default ComprehensionForm;
