import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const FormBuilder = ({ questions }) => {
  const [questionText, setQuestionText] = useState('');
  const [categories, setCategories] = useState([]);
  const [options, setOptions] = useState([]);
  const [newCategory, setNewCategory] = useState('');
  const [newOption, setNewOption] = useState('');
  const navigate = useNavigate();

  // Find the 'categorized' question from the passed `questions` prop
  const categorizedQuestion = questions.find(q => q.type === 'categorized');

  // Initialize the categories and options state based on the data passed from App.js
  useEffect(() => {
    if (categorizedQuestion) {
      setCategories(categorizedQuestion.data.categories);
      setOptions(categorizedQuestion.data.options);
    }
  }, [categorizedQuestion]);

  // Handle category selection
  const handleCategorySelection = (category) => {
    setCategories((prevCategories) => 
      prevCategories.includes(category)
        ? prevCategories.filter(c => c !== category)
        : [...prevCategories, category]
    );
  };

  // Handle option selection
  const handleOptionSelection = (option) => {
    setOptions((prevOptions) =>
      prevOptions.includes(option)
        ? prevOptions.filter(o => o !== option)
        : [...prevOptions, option]
    );
  };

  // Handle save action
  const handleSave = () => {
    const newQuestion = {
      type: 'categorized',
      data: {
        questionText,
        categories, // Save selected categories
        options, // Save selected options
      },
    };

    console.log('Saved Question:', newQuestion);
    navigate('/renderer');
  };

  return (
    <div className="p-4 border rounded shadow-md">
      <h2 className="text-xl font-bold mb-4">Form Builder</h2>

      {/* Question Text Input */}
      <div className="mb-4">
        <h3 className="font-semibold mb-2">Question Text</h3>
        <textarea
          value={questionText}
          onChange={(e) => setQuestionText(e.target.value)}
          placeholder="Enter your question text"
          className="border rounded p-2 w-full"
        />
      </div>

      {/* Categories Selection */}
      <div className="mb-4">
        <h3 className="font-semibold mb-2">Select Categories</h3>
        {categorizedQuestion?.data.categories.map((category, index) => (
          <div key={index} className="flex items-center gap-2 mb-2">
            <input
              type="checkbox"
              id={`category-${index}`}
              checked={categories.includes(category)}
              onChange={() => handleCategorySelection(category)}
              className="mr-2"
            />
            <label htmlFor={`category-${index}`} className="flex-grow">{category}</label>
          </div>
        ))}
      </div>

      {/* Options Selection */}
      <div className="mb-4">
        <h3 className="font-semibold mb-2">Select Options</h3>
        {categorizedQuestion?.data.options.map((option, index) => (
          <div key={index} className="flex items-center gap-2 mb-2">
            <input
              type="checkbox"
              id={`option-${index}`}
              checked={options.includes(option)}
              onChange={() => handleOptionSelection(option)}
              className="mr-2"
            />
            <label htmlFor={`option-${index}`} className="flex-grow">{option}</label>
          </div>
        ))}
      </div>

      {/* Save Button */}
      <button
        onClick={handleSave}
        className="mt-4 px-6 py-2 bg-green-500 text-white rounded"
      >
        Save Question
      </button>
    </div>
  );
};

export default FormBuilder;
